          );
        }
       } catch (err) {
          console.log(err);
          dispatch(fetchDataFailed("Could not load data from contract."));
        }
      };

    }
export const fetchData = (account) => {
  return async (dispatch) => {
    dispatch(fetchDataRequest());
    try {
      let allLips = await store
        .getState()
        .blockchain.lipToken.methods.getLips()
        .call();

      let allOwnerLips = await store
        .getState()
        .blockchain.lipToken.methods.getOwnerLips(account)
        .call();

      let owner  = await store
      .getState()
      .blockchain.lipToken.methods.ownerOf(allLips[10].id)
      .call();

      dispatch(
        fetchDataSuccess({
          allLips,
          allOwnerLips,
          owner,
        })
      );
    } catch (err) {
      console.log(err);
      dispatch(fetchDataFailed("Could not load data from contract."));
    }
  };
};
