import React, {useEffect , useState} from "react";
import { useDispatch, useSelector} from "react-redux";
import { connect, sendTransacsion} from "../redux/blockchain/blockchainActions"
import { fetchData , getOwner } from "../redux/data/dataActions";
import * as s from "../styles/globalStyles";
import LipRenderer from "./lipRenderer";

const Assets = (props) => {
    const dispatch = useDispatch();
    const blockchain = useSelector((state) => state.blockchain);
    const data = useSelector((state) => state.data);
    const [loading, setLoading] = useState(false);
    const [name, setName] = useState("");

    const mintNFT = (_account, _name) => {
        setLoading(true);
        blockchain.lipToken.methods
          .createRandomLip(_name)
          .send({
            from: _account,
            value: blockchain.web3.utils.toWei("0.01", "ether"),
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

      const addListingTrader = (_price, _account, _id) => {
        setLoading(true);
        blockchain.nftTrader.methods
          .addListing(_price, _account, _id)
          .send({
            from: blockchain.account,
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

      const purchase = (_constractAddr, _id) => {
        setLoading(true);
        blockchain.nftTrader.methods
          .purchase(_constractAddr, _id, 1)
          .send({
            from: blockchain.account,
            value: blockchain.web3.utils.toWei("0.01", "ether"),
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

      const BurnNft = ( _id) => {
        setLoading(true);
        blockchain.lipToken.methods
          ._burn(_id)
          .send({
            from: blockchain.account,
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

      const setApprovedForAll = (_contractAddr, _approved) => {
        setLoading(true);
        blockchain.lipToken.methods
          .setApprovalForAll(_contractAddr,_approved)
          .send({
            from: blockchain.account,
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

      const levelUpLip = (_account, _id) => {
        setLoading(true);
        blockchain.lipToken.methods
          .levelUp(_id)
          .send({
            from: _account,
          })
          .once("error", (err) => {
            setLoading(false);
            console.log(err);
          })
          .then((receipt) => {
            setLoading(false);
            console.log(receipt);
            dispatch(fetchData(blockchain.account));
          });
      };

    useEffect(() => {
        if (blockchain.account != "" && blockchain.lipToken != null) {
          dispatch(fetchData(blockchain.account));
        }
      }, [blockchain.lipToken]);

    return(
        <s.Container jc={"center"} fd={"row"} style={{ flexWrap: "wrap" }}>
            {data.allOwnerLips.map((item, index) => {
              return (
                <s.Container key={index} style={{ padding: "15px" }}>
                  <LipRenderer lip={item} />
                  <s.SpacerXSmall />
                  <s.Container>
                    <s.TextDescription>ID: {item.id}</s.TextDescription>
                    <s.TextDescription>DNA: {item.dna}</s.TextDescription>
                    <s.TextDescription>LEVEL: {item.level}</s.TextDescription>
                    <s.TextDescription>NAME: {item.name}</s.TextDescription>
                    <s.TextDescription>RARITY: {item.rarity}</s.TextDescription>
                    <s.SpacerXSmall />
                    {()=>{

                    }}

                  </s.Container>
                </s.Container>
              );
            })}
          </s.Container>
    )
}
export default Assets;
