// log
import store from "../store";
import Web3 from "web3";
import LipToken from "../../contracts/LipToken.json";

const fetchDataRequest = () => {
  return {
    type: "CHECK_DATA_REQUEST",
  };
};

const fetchDataSuccess = (payload) => {
  return {
    type: "CHECK_DATA_SUCCESS",
    payload: payload,
  };
};

const fetchDataFailed = (payload) => {
  return {
    type: "CHECK_DATA_FAILED",
    payload: payload,
  };
};
const getOwnerRequest = (payload) => {
  return {
    type: "OWNER",
    payload: payload,
  };
};

export const getOwner = (_id) => {
  return async (dispatch) => {
    dispatch(fetchDataRequest());
    try {
      let web3 = new Web3(window.ethereum);


      let owner = await store
      .getState()
      .blockchain.lipToken.methods.ownerOf(_id)
      .call();
      console.log("Hello");
      console.log(owner);
      dispatch(
        fetchDataSuccess({
          owner,
        })
      );
    } catch (err) {
      console.log(err);
      dispatch(fetchDataFailed("Could not load data from contract."));
    }
  }
}

export const fetchData = (account) => {
  return async (dispatch) => {
    dispatch(fetchDataRequest());
    try {
      let allLips = await store
        .getState()
        .blockchain.lipToken.methods.getLips()
        .call();

      let allOwnerLips = await store
        .getState()
        .blockchain.lipToken.methods.getOwnerLips(account)
        .call();

      let owner  = await store
      .getState()
      .blockchain.lipToken.methods.ownerOf(allLips[10].id)
      .call();

      dispatch(
        fetchDataSuccess({
          allLips,
          allOwnerLips,
          owner,
        })
      );
    } catch (err) {
      console.log(err);
      dispatch(fetchDataFailed("Could not load data from contract."));
    }
  };
};
